import leaguedashlineups2 from "../public/Data/leaguedashlineups2";
import leaguedashlineups3 from "../public/Data/leaguedashlineups3";
import leaguedashlineups4 from "../public/Data/leaguedashlineups4";
import leaguedashlineups5 from "../public/Data/leaguedashlineups5";
import React from 'react'
class statContainer extends React.Component {
    constructor(props) {
        super(props)
    }
    findRightRow (rowSet) {
        const nPlayers = this.props.lineup.length
        for(var iPlayer = 0; iPlayer < nPlayers; iPlayer++) {
            rowSet = rowSet.filter(row => row[2].toUpperCase().includes(this.props.lineup[iPlayer].lastName.toUpperCase()))
        }
        return rowSet
    }
    render () {
        const nPlayers = this.props.lineup.length
        var data = {}
        if (nPlayers > 5) {
            return <div> Too many players selected</div>
        } else if (nPlayers < 2) {
            return <div> Select atleast 2 players</div>
        } else if (nPlayers==2) {
            data = leaguedashlineups2
        } else if (nPlayers==3) {
            data = leaguedashlineups3
        } else if (nPlayers==4) {
            data = leaguedashlineups4
        } else if (nPlayers==5) {
            data = leaguedashlineups5
        }
        const rowToDisplay = this.findRightRow(data.resultSets[0].rowSet)
        if (rowToDisplay.length == 0) {
            return <div> This lineup never played together</div>
        }
        const playerNames = rowToDisplay[2]
        const statsToDisplay = rowToDisplay.slice(3,rowToDisplay.length)
        const headersToDisplay = data.resultSets[0].headers.slice(3,rowToDisplay.length)
        const header = headersToDisplay.map(name => <th key={name}>{name}</th>)
        const body = statsToDisplay.map(stat => <td key ={stat}>stat</td>)
        return (<div>{playerNames}<table>{header}{body}</table></div>)
    }
}

export default statContainer
